@use('main_page')

@part('style')
<style>
  * {
    font-family: Arial, Helvetica, sans-serif;
    padding: 0;
    margin: 0;
  }

  .container {
    color: #555555;
    margin: 30px auto;
    text-align: center;
    box-sizing: border-box;
    max-width: 820px;
  }

  .container h1 {
    margin-top: 20vh;
    margin-bottom: 0px;
    font-weight: 400;
    font-family: arial;
  }

  footer {
    margin-top: 24px;
    text-align: center;
    color: grey;
  }

  hr {
    border: 0;
    border-top: 1px solid #91919155;
    height: 1px;
    margin-top: 24px;
  }

  a {
    color: cornflowerblue;
    text-decoration: none;
  }

  .grid {
    margin-top: 28px;
    display: grid;
    grid-template-columns: 2fr 2fr;
    column-gap: 20px;
    row-gap: 24px;
    text-align: left;
    user-select: none;
  }

  .grid .card {
    line-height: 1.5;
    border: 1px solid #e9e9e9;
    padding: 12px 18px;
    border-radius: 8px;
    box-shadow: 0 1px 2px #8181817d;
    transition: 300ms ease-in-out;
  }
  .grid .card:hover{
    background-color: #00000010;
  }

  .grid h2 {
    font-size: medium;
    display: flex;
    margin-bottom: 8px;
    align-items: center;
    gap: 8px;
  }

  .grid p {
    text-align: justify;
  }
  svg {
    width: 25px;
    height: 25px;
  }
</style>
@endpart

@part('contents')
